function addItem(){$("#add-item-error").hide();var e=$("#add-item-title").val();$.post("/Todo/AddItem",{title:e},function(){window.location="/Todo"}).fail(function(e){if(e&&e.responseJSON){var t=e.responseJSON[Object.keys(e.responseJSON)[0]];$("#add-item-error").text(t),$("#add-item-error").show()}})}$(document).ready(function(){$("#add-item-button").on("click",addItem)});