function addItem(){$("#add-item-error").hide();var e=$("#add-item-title").val();$.post("/Todo/AddItem",{title:e},function(){window.location="/Todo"}).fail(function(e){if(e&&e.responseJSON){var t=e.responseJSON[Object.keys(e.responseJSON)[0]];$("#add-item-error").text(t),$("#add-item-error").show()}})}function markCompleted(e){e.disabled=!0,$.post("/Todo/MarkDone",{id:e.name},function(){var t=e.parentElement.parentElement;$(t).addClass("done")})}$(document).ready(function(){$("#add-item-button").on("click",addItem),$(".done-checkbox").on("click",function(e){markCompleted(e.target)})});